    description = "Parses a WhatsApp message from a Data input and provides multiple outputs for different parts of the message."
    icon = "message-square-code"
    name = "WAHAMessageParser"

    inputs = [
        DataInput(
            name="input_data",
            display_name="Input Data",
            info="A Data object containing a 'payload' field with the WhatsApp message data.",
        ),
    ]

    outputs = [
        Output(display_name="Full Message", name="full_message", method="full_message_response"),
        Output(display_name="Message ID", name="message_id", method="message_id_response"),
        Output(display_name="Timestamp", name="timestamp", method="timestamp_response"),
        Output(display_name="From", name="from", method="from_response"),
        Output(display_name="From Me", name="from_me", method="from_me_response"),
        Output(display_name="To", name="to", method="to_response"),
        Output(display_name="Participant", name="participant", method="participant_response"),
        Output(display_name="Body", name="body", method="body_response"),
        Output(display_name="Has Media", name="has_media", method="has_media_response"),
        Output(display_name="Media", name="media", method="media_response"),
        Output(display_name="Ack", name="ack", method="ack_response"),
