            logger.error("No 'payload' field found in the input Data object.")
            raise ValueError("Input Data object must contain a 'payload' field.")
        
        if not isinstance(payload, dict):
            logger.error(f"Invalid payload type: {type(payload)}. Expected dictionary.")
            raise ValueError("Payload must be a dictionary.")
        
