class DataBooleanSplitterComponent(Component):
    display_name = "Data Boolean Splitter"
    description = "Split flow based on a boolean key in the input Data"
    icon = "split"

    inputs = [
        DataInput(
            name="data_input",
            display_name="Data Input",
            info="The Data object to process",
        ),
        MessageTextInput(
            name="key_name",
            display_name="Key Name",
            info="The name of the key in the Data object to check for boolean value",
        ),
    ]

    outputs = [
        Output(display_name="True Output", name="true_output", method="process_data"),
        Output(display_name="False Output", name="false_output", method="process_data"),
    ]

    def parse_boolean(self, value):
        if isinstance(value, bool):
            return value
        if isinstance(value, str):
            return value.lower() in ['true', '1', 'yes', 'y', 'on']
        return bool(value)

    def validate_input(self):
        if not isinstance(self.data_input, Data):
            self.status = "Input is not a Data object"
            return False

        if self.key_name not in self.data_input.data:
            self.status = f"Key '{self.key_name}' not found in Data"
            return False

        return True

    def process_data(self) -> Data:
        if not self.validate_input():
            return Data(data={"error": self.status})

        boolean_value = self.parse_boolean(self.data_input.data[self.key_name])
        
        if boolean_value:
            self.status = f"Key '{self.key_name}' evaluated to True"
            self.stop("false_output")
            return self.data_input
        else:
            self.status = f"Key '{self.key_name}' evaluated to False"
            self.stop("true_output")
            return self.data_input

